[asy]unitsize(0.2inch);
draw((0,0)--(6,0)--(6,6)--(0,6)--cycle);
draw((1,0)--(1,6));
draw((2,0)--(2,6));
draw((3,0)--(3,6));
draw((4,0)--(4,6));
draw((5,0)--(5,6));
draw((0,1)--(6,1));
draw((0,2)--(6,2));
draw((0,3)--(6,3));
draw((0,4)--(6,4));
draw((0,5)--(6,5));

[/asy]
