unitsize(1cm);
draw((0,0)--(8,0),Arrows);
pair A,B,C,D,E;
A=(1,0); B=(8/3,0); C=(47/12,0);
D=(71/12,0); E=(7,0);
dot(A); dot(B); dot(C); dot(D); dot(E);
label("$A$",A,S);
label("$B$",B,S);
label("$C$",C,S);
label("$D$",D,S);
