unitsize(1 cm);

pair O, P, V, W;

O = (0,0);
V = (10/7,sqrt(3^2 - (10/7)^2));
P = (10/7,0);
W = (7,0);

draw(O--V,Arrow(6));
draw(O--W,Arrow(6));
draw(O--P,red,Arrow(6));
draw(P--V,dashed);

label("$\mathbf{v}$", V, N);
label("$\mathbf{w}$", W, E);
label("$\mathbf{p}$", P, S);
[/asy]
